export class OptionsMenu extends Phaser.Scene {
  private cursors!: Phaser.Types.Input.Keyboard.CursorKeys;
  private menuMusic: Phaser.Sound.BaseSound | null = null;
  private selectedOption = 0; // 0 = How to Play, 1 = Leaderboard, 2 = Back
  private menuItems: Phaser.GameObjects.Text[] = [];

  constructor() {
    super({ key: 'OptionsMenu' });
  }

  init(data: { menuMusic?: Phaser.Sound.BaseSound }) {
    // Receive menu music from MainMenu to keep it playing
    this.menuMusic = data.menuMusic || null;
  }

  create() {
    // Add graffiti background
    const bg = this.add.image(320, 480, 'graffiti_bg');
    bg.setDisplaySize(640, 960);

    // Title
    const titleText = this.add.text(320, 200, 'OPTIONS', {
      fontSize: '32px',
      color: '#ffecb3',
      fontFamily: '"Press Start 2P", monospace',
      align: 'center',
      stroke: '#000000',
      strokeThickness: 4
    }).setOrigin(0.5);
    titleText.setShadow(3, 3, '#000000', 5, true, true);

    // Menu options
    const howToPlayText = this.add.text(320, 320, 'HOW TO PLAY', {
      fontSize: '24px',
      color: '#ffffff',
      fontFamily: '"Press Start 2P", monospace',
      align: 'center',
      stroke: '#000000',
      strokeThickness: 3
    }).setOrigin(0.5);
    howToPlayText.setShadow(2, 2, '#000000', 3, true, true);

    const leaderboardText = this.add.text(320, 420, 'LEADERBOARD', {
      fontSize: '24px',
      color: '#ffffff',
      fontFamily: '"Press Start 2P", monospace',
      align: 'center',
      stroke: '#000000',
      strokeThickness: 3
    }).setOrigin(0.5);
    leaderboardText.setShadow(2, 2, '#000000', 3, true, true);

    const backText = this.add.text(320, 520, 'GO BACK', {
      fontSize: '22px',
      color: '#ffffff',
      fontFamily: '"Press Start 2P", monospace',
      align: 'center',
      stroke: '#000000',
      strokeThickness: 2
    }).setOrigin(0.5);
    backText.setShadow(2, 2, '#000000', 3, true, true);

    // Store menu items
    this.menuItems = [howToPlayText, leaderboardText, backText];

    // Selection indicator
    const selector = this.add.text(150, 320, '>', {
      fontSize: '24px',
      color: '#ffff00',
      fontFamily: '"Press Start 2P", monospace',
      stroke: '#000000',
      strokeThickness: 3
    }).setOrigin(0.5);
    selector.setShadow(2, 2, '#000000', 3, true, true);

    // Update selector position
    const updateSelector = () => {
      // Reset all colors with font preserved
      howToPlayText.setStyle({ color: '#ffffff', fontFamily: '"Press Start 2P", monospace' });
      leaderboardText.setStyle({ color: '#ffffff', fontFamily: '"Press Start 2P", monospace' });
      backText.setStyle({ color: '#ffffff', fontFamily: '"Press Start 2P", monospace' });

      if (this.selectedOption === 0) {
        selector.setY(320);
        howToPlayText.setStyle({ color: '#00ff00', fontFamily: '"Press Start 2P", monospace' });
      } else if (this.selectedOption === 1) {
        selector.setY(420);
        leaderboardText.setStyle({ color: '#00ff00', fontFamily: '"Press Start 2P", monospace' });
      } else {
        selector.setY(520);
        backText.setStyle({ color: '#00ff00', fontFamily: '"Press Start 2P", monospace' });
      }
    };

    updateSelector();

    // Make options interactive
    howToPlayText.setInteractive({ useHandCursor: true })
      .on('pointerdown', () => {
        this.scene.start('HowToPlay', { menuMusic: this.menuMusic });
      })
      .on('pointerover', () => {
        this.selectedOption = 0;
        updateSelector();
      });

    leaderboardText.setInteractive({ useHandCursor: true })
      .on('pointerdown', () => {
        this.scene.start('Leaderboard', { menuMusic: this.menuMusic });
      })
      .on('pointerover', () => {
        this.selectedOption = 1;
        updateSelector();
      });

    backText.setInteractive({ useHandCursor: true })
      .on('pointerdown', () => {
        this.scene.start('MainMenu', { menuMusic: this.menuMusic });
      })
      .on('pointerover', () => {
        this.selectedOption = 2;
        updateSelector();
      });

    // Handle keyboard input
    const upKey = this.input.keyboard?.addKey('UP');
    const downKey = this.input.keyboard?.addKey('DOWN');
    const spaceKey = this.input.keyboard?.addKey('SPACE');
    const enterKey = this.input.keyboard?.addKey('ENTER');

    upKey?.on('down', () => {
      this.selectedOption = (this.selectedOption - 1 + this.menuItems.length) % this.menuItems.length;
      updateSelector();
    });

    downKey?.on('down', () => {
      this.selectedOption = (this.selectedOption + 1) % this.menuItems.length;
      updateSelector();
    });

    const selectOption = () => {
      if (this.selectedOption === 0) {
        this.scene.start('HowToPlay', { menuMusic: this.menuMusic });
      } else if (this.selectedOption === 1) {
        this.scene.start('Leaderboard', { menuMusic: this.menuMusic });
      } else {
        this.scene.start('MainMenu', { menuMusic: this.menuMusic });
      }
    };

    spaceKey?.on('down', selectOption);
    enterKey?.on('down', selectOption);

    // Set up input
    this.cursors = this.input.keyboard!.createCursorKeys();
  }

  update() {
    // ESC to go back
    if (Phaser.Input.Keyboard.JustDown(this.input.keyboard!.addKey('ESC'))) {
      this.scene.start('MainMenu', { menuMusic: this.menuMusic });
    }
  }
}

client/src/game/scenes/Splash1.ts.content:
export class Splash1 extends Phaser.Scene {
  constructor() {
    super({ key: 'Splash1' });
  }

  create() {
    // Set black background
    this.cameras.main.setBackgroundColor('#000000');

    const centerX = this.cameras.main.width / 2;
    const centerY = this.cameras.main.height / 2;

    // Display the Soul Arcade Advance logo - make it bigger
    const logo = this.add.image(centerX, centerY, 'soul_arcade_new_logo');
    logo.setOrigin(0.5, 0.5);

    // Make the logo bigger - use more of the screen
    const maxWidth = 1600;  // Much bigger
    const maxHeight = 1400; // Much bigger
    const scale = Math.min(maxWidth / logo.width, maxHeight / logo.height);
    logo.setScale(scale);

    // Wait 2 seconds then add shine effect
    this.time.delayedCall(2000, () => {
      // Create a duplicate of the logo for the shine effect
      const shineLogo = this.add.image(centerX, centerY, 'soul_arcade_new_logo');
      shineLogo.setOrigin(0.5, 0.5);
      shineLogo.setScale(scale);
      shineLogo.setAlpha(0); // Start invisible

      // Use additive blend mode so only the colored parts (letters) get brightened
      // Black stays black since black + anything = that color
      shineLogo.setBlendMode(Phaser.BlendModes.ADD);
      shineLogo.setTint(0xffffff); // White tint for shine

      // Create a gradient mask for the shine sweep
      const maskGraphics = this.make.graphics({});
      const shineWidth = 150;

      // Start position - off the left side
      let maskX = logo.x - logo.displayWidth/2 - shineWidth;

      // Create initial mask
      maskGraphics.fillStyle(0xffffff);
      maskGraphics.fillRect(maskX, logo.y - logo.displayHeight/2, shineWidth, logo.displayHeight);

      const mask = maskGraphics.createGeometryMask();
      shineLogo.setMask(mask);

      // Make the shine visible
      shineLogo.setAlpha(0.6);

      // Animate the mask moving across
      this.tweens.add({
        targets: { x: maskX },
        x: logo.x + logo.displayWidth/2 + shineWidth,
        duration: 800,
        ease: 'Power2',
        onUpdate: (tween) => {
          const value = tween.targets[0] as any;
          maskGraphics.clear();
          maskGraphics.fillStyle(0xffffff);
          // Create a gradient effect by drawing multiple rectangles with decreasing alpha
          const gradientWidth = shineWidth;
          const strips = 10;
          for (let i = 0; i < strips; i++) {
            const alpha = 1 - (i / strips);
            maskGraphics.fillStyle(0xffffff, alpha);
            maskGraphics.fillRect(
              value.x + (i * gradientWidth/strips), 
              logo.y - logo.displayHeight/2, 
              gradientWidth/strips, 
              logo.displayHeight
            );
          }
        },
        onComplete: () => {
          shineLogo.destroy();
          maskGraphics.destroy();

          // Transition to next splash screen after shine completes
          this.time.delayedCall(500, () => {
            this.scene.start('Splash2');
          });
        }
      });
    });
  }
}

